import styled from "styled-components";

const Container = styled.div`
    display: flex;
    align-items: center;
    justify-content: center;
    flex-direction: column;

    & h1 {
        font-family: "Saira Stencil One", cursive;
        font-size: 32px;
    }

    & a {
        font-weight: bold;
    }
`;

export { Container };
