import styled from "styled-components";

const Container = styled.div`
    display: flex;
    align-items: center;
    justify-content: center;
    flex-direction: column;

    & h1 {
        font-size: 32px;
        font-family: "Saira Stencil One", cursive;
    }

    & a {
        font-size: 15px;
        font-weight: 700;
    }
`;

const Button = styled.div``;

export { Container };
